mapper.update:
UPDATE priority SET is_default = 1
AND id = (
 select t.id from(
     select min(p.id) as id from priority p
     WHERE p.organization_id=#{organizationId}
     AND p.is_enable = 1
 ) t
)

mapper.select:
SELECT count(*) FROM priority
WHERE organization_id = #{organizationId}
and is_default = 1
